INSERT INTO `v9_page` VALUES('52','组织架构',';','组织架构','<span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 陕西中信蓝海企业管理有限公司系有限责任公司，成立于2016年4月11日，位于陕西省西安市曲江新区雁南五路影视大厦10层，集团总部占地面积1500m<sup>2</sup>，全集团合计办公面积约占地约10000㎡。集团公司下设董事长办公室，常务总裁办公室，总经理办公室、总监、财务部、经营部、市场发展部。集团旗下有创业管理板块、项目管理板块、建筑设计板块、农业发展四大板块，共计九个分公司，由集团总部统一管理。<br />\r\n&nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://www.zxlh.asia/uploadfile/2016/0817/20160817105351862.png\" /></span><br />\r\n<br />\r\n<br />\r\n','','0');
