INSERT INTO `v9_page` VALUES('150','资质展示',';','','<style type=\"text/css\">\r\n*{margin:0;padding:0;list-style: none;border:0;}\r\n.container { \r\n    margin:50px 0;\r\n    padding: 0 !important;\r\n    overflow: hidden;\r\n}\r\n.poster-main{ position:relative;}\r\n.poster-main a,.poster-main img{display:block;}\r\n.poster-main .poster-list .poster-item{ position:absolute;left:0;top:0;overflow: hidden;border:0;}\r\n.poster-main .poster-btn{ position:absolute;top:0;width:100px;height:100px; z-index:10; cursor:pointer;  opacity:0.8}\r\n.poster-main .poster-prev-btn{left:0; background: url(http://demo.lanrenzhijia.com/2016/banner0226/images/btn_l.png) no-repeat center center;}\r\n.poster-main .poster-next-btn{right:0; background: url(http://demo.lanrenzhijia.com/2016/banner0226/images/btn_r.png) no-repeat center center;}</style>\r\n<div class=\"container\">\r\n<div class=\"pictureSlider poster-main\">\r\n<div class=\"poster-btn poster-prev-btn\">&nbsp;</div>\r\n<ul class=\"poster-list\">\r\n<li class=\"poster-item\"><a href=\"#\"><img src=\"http://obxi72nz0.bkt.clouddn.com/16-10-13/14113280.jpg\" width=\"400px\" /></a></li>\r\n<li class=\"poster-item\"><a href=\"#\"><img src=\"http://obxi72nz0.bkt.clouddn.com/16-10-13/37441300.jpg\" width=\"400px\" /></a></li>\r\n<li class=\"poster-item\"><a href=\"#\"><img src=\"http://obxi72nz0.bkt.clouddn.com/16-10-13/94524259.jpg\" width=\"400px\" /></a></li>\r\n</ul>\r\n<div class=\"poster-btn poster-next-btn\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<a href=\"#\"><script>\r\n;(function($){\r\n\r\n    var Carousel = function(poster){\r\n        var self = this;\r\n        //保存单个旋转木马对象\r\n        this.poster = poster;\r\n        this.posterItemMain = poster.find(\"ul.poster-list\");\r\n        this.nextBtn = poster.find(\"div.poster-next-btn\");\r\n        this.prevBtn = poster.find(\"div.poster-prev-btn\");\r\n        this.posterItems = poster.find(\"li.poster-item\");\r\n        if( this.posterItems.size()%2 == 0 ){\r\n            this.posterItemMain.append( this.posterItems.eq(0).clone() );\r\n            this.posterItems = this.posterItemMain.children();\r\n        };\r\n        this.posterFirstItem = this.posterItems.first();\r\n        this.posterLastItem = this.posterItems.last();\r\n        this.rotateFlag = true;\r\n        //默认配置参数\r\n        this.setting = {\r\n            \"width\" : 920,         //幻灯片的宽度\r\n            \"height\" : 460,         //幻灯片的高度\r\n            \"posterWidth\" : 520,    //幻灯片第一帧的宽度\r\n            \"posterHeight\" : 450,   //幻灯片第一帧的高度\r\n            \"scale\" : 0.9,          //记录显示比例关系\r\n            \"speed\" : 500,\r\n            \"autoPlay\" : false,\r\n            \"delay\" : 5000,\r\n            \"verticalAlign\" : \"middle\" //top bottom\r\n        };\r\n        $.extend( this.setting,this.getSetting() );\r\n        \r\n        //设置配置参数值\r\n        this.setSettingValue();\r\n        //初始化幻灯片位置\r\n        this.setPosterPos();\r\n        //左旋转按钮\r\n        this.nextBtn.click(function(){\r\n            if(self.rotateFlag){\r\n                self.rotateFlag = false;\r\n                self.carouseRotate(\"left\");\r\n            };\r\n        });\r\n        //右旋转按钮\r\n        this.prevBtn.click(function(){\r\n            if(self.rotateFlag){\r\n                self.rotateFlag = false;\r\n                self.carouseRotate(\"right\");\r\n            };\r\n        });\r\n        //是否开启自动播放\r\n        if(this.setting.autoPlay){\r\n            this.autoPlay();\r\n            this.poster.hover( function(){\r\n                //self.timer是setInterval的种子\r\n                window.clearInterval(self.timer);\r\n            }, function(){\r\n                self.autoPlay();\r\n            });         \r\n        };\r\n    };\r\n    Carousel.prototype = {\r\n        autoPlay:function(){\r\n            var self = this;\r\n            this.timer = window.setInterval( function(){\r\n                self.nextBtn.click();\r\n            }, this.setting.delay );\r\n        },\r\n\r\n        //旋转\r\n        carouseRotate:function(dir){\r\n            var _this_  = this;\r\n            var zIndexArr = [];\r\n            //左旋转\r\n            if(dir === \"left\"){\r\n                this.posterItems.each(function(){\r\n                    var self = $(this),\r\n                        prev = self.prev().get(0)?self.prev():_this_.posterLastItem,\r\n                        width = prev.width(),\r\n                        height =prev.height(),\r\n                        opacity = prev.css(\"opacity\"),\r\n                        left = prev.css(\"left\"),\r\n                        top = prev.css(\"top\"),\r\n                        zIndex = prev.css(\"zIndex\");\r\n\r\n                    zIndexArr.push(zIndex);\r\n                    self.animate({\r\n                        width :width,\r\n                        height :height,\r\n                      //zIndex :zIndex,\r\n                        opacity :opacity,\r\n                        left :left,\r\n                        top :top\r\n                    },_this_.setting.speed,function(){\r\n                        _this_.rotateFlag = true;\r\n                    });\r\n                });\r\n                //zIndex需要单独保存再设置，防止循环时候设置再取的时候值永远是最后一个的zindex\r\n                this.posterItems.each(function(i){\r\n                    $(this).css(\"zIndex\",zIndexArr[i]);\r\n                });\r\n            }else if(dir === \"right\"){//右旋转\r\n                this.posterItems .each(function(){\r\n                    var self = $(this),\r\n                        next = self.next().get(0)?self.next():_this_.posterFirstItem,\r\n                        width = next.width(),\r\n                        height =next.height(),\r\n                        opacity = next.css(\"opacity\"),\r\n                        left = next.css(\"left\"),\r\n                        top = next.css(\"top\"),\r\n                        zIndex = next.css(\"zIndex\");\r\n\r\n                    zIndexArr.push(zIndex);                 \r\n                    self.animate({\r\n                        width :width,\r\n                        height :height,\r\n                      //zIndex :zIndex,\r\n                        opacity :opacity,\r\n                        left :left,\r\n                        top :top\r\n                    },_this_.setting.speed,function(){\r\n                        _this_.rotateFlag = true;\r\n                    }); \r\n                });\r\n                //zIndex需要单独保存再设置，防止循环时候设置再取的时候值永远是最后一个的zindex\r\n                this.posterItems.each(function(i){\r\n                    $(this).css(\"zIndex\",zIndexArr[i]);\r\n                });\r\n            };\r\n        },\r\n        //设置剩余的帧的位置关系\r\n        setPosterPos:function(){\r\n            var self = this,\r\n                sliceItems = this.posterItems.slice(1),\r\n                sliceSize = sliceItems.size()/2,\r\n                rightSlice = sliceItems.slice(0,sliceSize),\r\n                //存在图片奇偶数问题\r\n                level = Math.floor(this.posterItems.size()/2),\r\n                leftSlice = sliceItems.slice(sliceSize);\r\n            \r\n            //设置右边帧的位置关系和宽度高度top\r\n            var firstLeft = (this.setting.width - this.setting.posterWidth)/2;\r\n            var rw = this.setting.posterWidth,\r\n                fixOffsetLeft = firstLeft + rw,\r\n                rh = this.setting.posterHeight,\r\n                gap = ((this.setting.width - this.setting.posterWidth)/2)/level;\r\n            \r\n            //设置右边位置关系\r\n            rightSlice.each(function(i){\r\n                level--;\r\n                rw = rw * self.setting.scale;\r\n                rh = rh * self.setting.scale;\r\n                var j = i;\r\n                $(this).css({\r\n                    zIndex :level,\r\n                    width :rw,\r\n                    height :rh,\r\n                    opacity :1/(++j),\r\n                    left :fixOffsetLeft+(++i)*gap - rw,\r\n                    top :self.setVerticalAlign(rh)\r\n                });\r\n            });\r\n\r\n            //设置左边的位置关系\r\n            var lw = rightSlice.last().width(),\r\n                lh  =rightSlice.last().height(),\r\n                oloop = Math.floor(this.posterItems.size()/2);\r\n            leftSlice.each(function(i){\r\n                $(this).css({\r\n                    zIndex:i,\r\n                    width:lw,\r\n                    height:lh,\r\n                    opacity:1/oloop,\r\n                    left:i*gap,\r\n                    top:self.setVerticalAlign(lh)\r\n                });\r\n                lw = lw/self.setting.scale;\r\n                lh = lh/self.setting.scale;\r\n                oloop--;\r\n            });\r\n        },\r\n    \r\n        //设置垂直排列对齐\r\n        setVerticalAlign:function(height){\r\n            var verticalType  = this.setting.verticalAlign,\r\n                top = 0;\r\n            if(verticalType === \"middle\"){\r\n                top = (this.setting.height-height)/2;\r\n            }else if(verticalType === \"top\"){\r\n                top = 0;\r\n            }else if(verticalType === \"bottom\"){\r\n                top = this.setting.height-height;\r\n            }else{\r\n                top = (this.setting.height-height)/2;\r\n            };\r\n            return top;\r\n        },\r\n\r\n        //设置配置参数值去控制基本的宽度高度。。。\r\n        setSettingValue:function(){\r\n            this.poster.css({\r\n                width:this.setting.width,\r\n                height:this.setting.height\r\n            });\r\n            this.posterItemMain.css({\r\n                width:this.setting.width,\r\n                height:this.setting.height\r\n            });\r\n            //计算上下切换按钮的宽度\r\n            var w = (this.setting.width-this.setting.posterWidth)/2;\r\n            //设置切换按钮的宽高，层级关系\r\n            this.nextBtn.css({\r\n                width:w,\r\n                height:this.setting.height,\r\n                zIndex:Math.ceil(this.posterItems.size()/2)\r\n            });\r\n            this.prevBtn.css({\r\n                width:w,\r\n                height:this.setting.height,\r\n                zIndex:Math.ceil(this.posterItems.size()/2)\r\n            });         \r\n            this.posterFirstItem.css({\r\n                width:this.setting.posterWidth,\r\n                height:this.setting.posterHeight,\r\n                left:w,\r\n                top:0,\r\n                zIndex:Math.floor(this.posterItems.size()/2)\r\n            });\r\n        },\r\n\r\n        //获取人工配置参数\r\n        getSetting:function(){          \r\n            var setting = this.poster.attr(\"data-setting\");\r\n            if(setting && setting != \"\"){\r\n                return $.parseJSON(setting);\r\n            }else{\r\n                return {};\r\n            };\r\n        }   \r\n    };\r\n\r\n    Carousel.init = function(posters){\r\n        var _this_ = this;\r\n        posters.each(function(){\r\n            // console.log(\"halo Louis;\")\r\n            new  _this_($(this));\r\n        });\r\n    };\r\n\r\n    //挂载到window下\r\n    window.Carousel = Carousel;\r\n\r\n})(jQuery);\r\n</script><script>\r\n$(function(){\r\n	Carousel.init($(\".pictureSlider\"));\r\n});\r\n</script></a>','','0');
