INSERT INTO `v9_picture_data` VALUES('100','<br />\r\n<span style=\"font-size:14px;\"><span style=\"color:#ead527;\"><strong>-- &nbsp;创业就是勇于去做不可能的事 &nbsp;--</strong></span><br />\r\n每个人的成功都不会是一帆风顺的，<br />\r\n其中需要艰辛的付出，充足的谋略和胆识，<br />\r\n也需要足够的机遇和勇气。<br />\r\n<span style=\"color:#ead527;\"><strong>-- &nbsp;十二句话 &nbsp;--</strong></span><br />\r\n第一句：创业就是要勇于去做不可能的事；<br />\r\n第二句：创造价值才有机会成功；<br />\r\n第三句：创业是要解决问题，只想赚钱，出名，自由的创业者注定失败；<br />\r\n第四句：必须爱上你所在的行业，且要深爱；<br />\r\n第五句：人脉太多反而可能拖垮你的企业；<br />\r\n第六句：失去对企业的控制权，等于失去的一切；<br />\r\n第七句：宁可忍受暂时不赚钱的羞愧，也不能持续的愚蠢；<br />\r\n第八句：不要把时间浪费在股权比例上；<br />\r\n第九句：创业失败的根本原因只有一个，团队不行，其他都是借口；<br />\r\n第十句：把简单的事情做到极致才能天下无敌；<br />\r\n第十一句：老祖宗有句古话是真理，不求暴利，细水长流；<br />\r\n第十二句：企业必须合法的，干净的成功，让员工有尊严的生活。</span> ','0','','0','10000','','0','','','|1','1');
INSERT INTO `v9_picture_data` VALUES('101','<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:14px;\"><span style=\"color:#ead527;\"><strong>-- &nbsp;创业就业 改变观念 服务三农 公益事业 &nbsp;--</strong></span><br />\r\n企业必须承担社会责任，而什么是社会责任？<br />\r\n跟正直善良的一帮人，互相关爱、尊重的一帮人，一起合法干净的成功，<br />\r\n过有尊严、有信心的生活，就是我们最实在的社会责任。</span> ','0','','0','10000','','0','','','|0','1');
INSERT INTO `v9_picture_data` VALUES('102','<br />\r\n<br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:14px;\"><span style=\"color:#ead527;\"><strong>为梦想而来：</strong></span>一个人至少有一个梦想，有一个理由去坚强，心若没有栖息的地方，到哪里都是流浪！--项目管理公司总经理 &nbsp;郭春洋<br />\r\n<span style=\"color:#ead527;\"><strong>为成就而来：</strong></span>所有伟大的成就，都是战胜了看来不可能的事情而取得的，我为成就而来！--春禾绿科运营总监 &nbsp;郝巨杰<br />\r\n<span style=\"color:#ead527;\"><strong>为奉献而来：</strong></span>奉献是一种不求回报的给予，我们的奉献永不止步！--同乘设计营销总监 李栋<br />\r\n<span style=\"color:#ead527;\"><strong>为快乐而来：</strong></span>认真生活，快乐工作！--华春项目管理技术总监 &nbsp;刘旦<br />\r\n<span style=\"color:#ead527;\"><strong>为极致而来：</strong></span>100-1=0！--华春项目管理西安区营销总监 张博<br />\r\n<span style=\"color:#ead527;\"><strong>为感恩而来：</strong></span>我们生活在感恩的世界里，感谢一切使自己坚定的人！--华春项目管理延安区营销总监 张洋<br />\r\n<span style=\"color:#ead527;\"><strong>为幸福而来：</strong></span>选择创业，就要忍受痛苦，但这份痛苦是幸福的！--华春项目管理榆林区营销总监 赵芳<br />\r\n<span style=\"color:#ead527;\"><strong>为荣耀而来：</strong></span>成就梦想，共享荣耀！--集团董事长 郭春河</span> ','0','','0','10000','','0','','','|1','1');
